import Navbar from "./Navbar";
import NewsFeedItem from "./NewsFeedItem";
import styles from "./Layout.module.css";
import FavoriteItem from "./FavoriteItem";

export const siteTitle = "Demo App";

export default class Layout extends React.Component {
  constructor(props) {
    super();
    this.state = {
      newsFeedItemList: [
        {
          index: 0,
          src: "./tiger.jpg",
          title: "Tiger",
          id: "1_tiger",
          isLiked: false,
        },
        {
          index: 1,
          src: "./turtle.jpg",
          title: "Turtle",
          id: "2_turtle",
          isLiked: false,
        },
        {
          index: 2,
          src: "./sambar-deer.jpg",
          title: "Samber Deer",
          id: "3_deer",
          isLiked: false,
        },
        {
          index: 3,
          src: "./squirrel.jpg",
          title: "Squrrel",
          id: "4_squirrel",
          isLiked: false,
        },
        {
          index: 4,
          src: "./pupy.jpg",
          title: "Pupy",
          id: "5_pupy",
          isLiked: false,
        },
      ],
    };
  }

  handleLikedItemList = (itemArr) => {
    const newsFeedItemList = [...this.state.newsFeedItemList];
    const index = itemArr.forEach((element) => {
      newsFeedItemList[element.index].isLiked = true;
    });

    this.setState({ newsFeedItemList });
  };

  render() {
    const { newsFeedItemList, favoriteItemList } = this.state;
    const { children, home } = this.props;

    return (
      <div>
        <Navbar />
        <div className={styles.topClearance}>
          {home ? (
            newsFeedItemList.map((item, key) => {
              return (
                <NewsFeedItem
                  key={key}
                  detail={item}
                  handleNewsFeedItem={this.handleNesFeedItemList}
                />
              );
            })
          ) : favoriteItemList.length === 0 ? (
            <FavoriteItem detail={favoriteItemList.length} />
          ) : (
            newsFeedItemList.map((item, key) => {
              if (item.isLiked) {
                return;
                <FavoriteItem
                  key={key}
                  detail={item}
                  handleLikedList={this.handleLikedItemList}
                />;
              }
            })
          )}
        </div>
        {children}
      </div>
    );
  }
}
