import Navbar from "./Navbar";
import NewsFeedItem from "./NewsFeedItem";
import styles from "./Layout.module.css";
import FavoriteItem from "./FavoriteItem";

class Layout extends React.Component {
  constructor(props) {
    super();
    this.siteTitle = "Demo App";
    this.state = {
      newsFeedItemList: [
        { src: "./tiger.jpg", title: "Tiger", id: "1_tiger" },
        { src: "./turtle.jpg", title: "Turtle", id: "2_turtle" },
        { src: "./sambar-deer.jpg", title: "Samber Deer", id: "3_deer" },
        { src: "./squirrel.jpg", title: "Squrrel", id: "4_squirrel" },
        { src: "./pupy.jpg", title: "Pupy", id: "5_pupy" },
      ],
      favoriteItemList: [],
    };
  }

  handleLikedItemList = (list) => {};

  render() {
    const { newsFeedItemList, favoriteItemList } = this.state;

    return (
      <div>
        <Navbar />
        <div className={styles.topClearance}>
          {home
            ? newsFeedItemList.map((item, key) => {
                return <NewsFeedItem key={key} detail={item} />;
              })
            : favoriteItemList.map((item, key) => {
                return (
                  <FavoriteItem
                    key={key}
                    detail={item}
                    handleLikedList={this.handleLikedItemList}
                  />
                );
              })}
        </div>
        {children}
      </div>
    );
  }
}

export default Layout;
