import { Card } from "react-bootstrap";
import styles from "./NewsFeedItem.module.css";
import fetch from "isomorphic-fetch";

class NewsFeedItem extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      isLike: false,
    };
  }

  async sendDataToBackend() {
    const requestObj = {};
    const { id } = this.props.detail;

    requestObj.Id = id;
    requestObj.isLike = this.state.isLike;

    const requestOptions = {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(requestObj),
    };

    const response = await fetch(
      "http://localhost:9000/favorite/update",
      requestOptions
    );
    const data = await response.json();
  }

  heartClickHandler = () => {
    this.setState(
      {
        isLike: !this.state.isLike,
      },
      this.updateHeart
    );
  };

  updateHeart = () => {
    const { id } = this.props.detail;

    const heart = document.getElementById(id);
    const addClass = this.state.isLike ? "fa-heart" : "fa-heart-o";
    const removeClass = this.state.isLike ? "fa-heart-o" : "fa-heart";

    heart.classList.remove(removeClass);
    heart.classList.add(addClass);

    this.sendDataToBackend();
  };

  render() {
    const { detail } = this.props;

    return (
      <div className={styles.cardPosition}>
        <Card>
          <Card.Img
            className={styles.imageSize}
            variant="top"
            src={detail.src}
          />
          <Card.Body>
            <span onClick={this.heartClickHandler}>
              <i
                id={detail.id}
                className="fa fa-heart-o"
                aria-hidden="true"
              ></i>
            </span>
            <Card.Title>{detail.title}</Card.Title>
            <Card.Text>
              This is a wider card with supporting text below as a natural
              lead-in to additional content. This content is a little bit
              longer.
            </Card.Text>
          </Card.Body>
          <Card.Footer>
            <small className="text-muted">Last updated 3 mins ago</small>
          </Card.Footer>
        </Card>
        <style jsx>{`
          .fa-heart-o {
            color: red;
            cursor: pointer;
          }

          .fa-heart {
            color: red;
            cursor: pointer;
          }
        `}</style>
      </div>
    );
  }
}

export default NewsFeedItem;
