import Head from "next/head";
import Layout from "../component/Layout";
import FavoriteItem from "../component/FavoriteItem";
import fetch from "isomorphic-fetch";

const Favorite = (props) => {
  return (
    <Layout>
      <div>
        <Head>
          <title>Favorites</title>
        </Head>
        {Object.values(props.favItem[0]).length > 0 ? (
          props.favItem.forEach((element, key) => {
            <FavoriteItem key={key} detail={item} />;
          })
        ) : (
          <FavoriteItem detail={props.favItem[0].length} />
        )}
        }
      </div>
    </Layout>
  );
};

Favorite.getInitialProps = async function () {
  const res = await fetch("http://localhost:9000/favorite");
  const data = await res.json();

  return {
    favItem: data,
  };
};
