import Head from "next/head";
import Layout from "../component/Layout";
import FavoriteItem from "../component/FavoriteItem";
import fetch from "isomorphic-fetch";
import styles from "../public/utils.module.css";
import { Card, Button } from "react-bootstrap";

class Favorite extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      favItemList: [],
    };
  }

  static async getInitialProps() {
    const res = await fetch("http://localhost:9000/favorite");
    const data = await res.json();

    return {
      favItem: data,
    };
  }

  render() {
    const { favItemList } = this.state;

    return (
      <Layout>
        <div>
          <Head>
            <title>Favorites</title>
          </Head>
          <div className={styles.cardPosition}>
            <Card>
              <Card.Header>Featured</Card.Header>
              <Card.Body>
                <Card.Title>Special title treatment</Card.Title>
                <Card.Text>
                  With supporting text below as a natural lead-in to additional
                  content.
                </Card.Text>
                <Button variant="primary">Show Favorite</Button>
              </Card.Body>
            </Card>
          </div>
          {/* <div className={styles.topClearance}>
            {favItemList.length > 0 ? (
              favItemList.forEach((element, key) => {
                <FavoriteItem key={key} detail={element} />;
              })
            ) : (
              <FavoriteItem />
            )}
          </div> */}
          <h1>{this.props.favItem[0].src}</h1>
        </div>
      </Layout>
    );
  }
}

export default Favorite;
