import Head from "next/head";
import Layout from "../component/Layout";
import FavoriteItem from "../component/FavoriteItem";
import fetch from "isomorphic-fetch";
import styles from "../public/utils.module.css";

class Favorite extends React.Component {
  constructor(props) {
    super(props);

    this.state = {
      favItemList: [],
    };
  }

  static async getInitialProps() {
    var favData;

    fetch("http://localhost:9000/favorite")
      .then((response) => response.json())
      .then((data) => {
        favData = data;
        this.setState({ favItemList: data });
        return { favData };
      });

    return {
      favItem: favData,
    };
  }

  render() {
    const { favItemList } = this.state;

    return (
      <Layout>
        <div>
          <Head>
            <title>Favorites</title>
          </Head>
          <div className={styles.topClearance}>
            {favItemList.length > 0 ? (
              favItemList.forEach((element, key) => {
                <FavoriteItem key={key} detail={element} />;
              })
            ) : (
              <FavoriteItem />
            )}
          </div>
        </div>
      </Layout>
    );
  }
}

export default Favorite;
