import Head from "next/head";
import Layout from "../component/Layout";
import FavoriteItem from "../component/FavoriteItem";
import fetch from "isomorphic-fetch";
import styles from "../public/utils.module.css";

class Favorite extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      numOfFavItem: 0,
    };
    Favorite.getInitialProps = FavoriteItem.getInitialProps.bind(this);
  }
  componentWillReceiveProps() {
    console.log("this is change");
    this.setState({ numOfFavItem: this.props.favItem.length });
  }

  static async getInitialProps() {
    const res = await fetch("http://localhost:9000/favorite");
    const data = await res.json();

    console.log(this.state);

    return { favItem: data };
  }

  render() {
    const { favItem } = this.props;

    return (
      <Layout>
        <div>
          <Head>
            <title>Favorites</title>
          </Head>
          <div className={styles.topClearance}>
            {favItem.length > 0 ? (
              favItem.forEach((element, key) => {
                <FavoriteItem key={key} detail={element} />;
              })
            ) : (
              <FavoriteItem />
            )}
          </div>
        </div>
      </Layout>
    );
  }
}

export default Favorite;
