import Navbar from "./Navbar";
import NewsFeedItem from "./NewsFeedItem";
import styles from "./Layout.module.css";
import FavoriteItem from "./FavoriteItem";

export const siteTitle = "Demo App";

export default class Layout extends React.Component {
  constructor(props) {
    super();
    this.state = {
      newsFeedItemList: [
        {
          index: 0,
          src: "./tiger.jpg",
          title: "Tiger",
          id: "1_tiger",
          isLiked: false,
        },
        {
          index: 1,
          src: "./turtle.jpg",
          title: "Turtle",
          id: "2_turtle",
          isLiked: false,
        },
        {
          index: 2,
          src: "./sambar-deer.jpg",
          title: "Samber Deer",
          id: "3_deer",
          isLiked: false,
        },
        {
          index: 3,
          src: "./squirrel.jpg",
          title: "Squrrel",
          id: "4_squirrel",
          isLiked: false,
        },
        {
          index: 4,
          src: "./pupy.jpg",
          title: "Pupy",
          id: "5_pupy",
          isLiked: false,
        },
      ],

      numOfFavoriteItem: 0,
    };
  }

  async callAPI() {}

  handleNewsFeedItemList = (itemArr) => {
    const newsFeedItemList = [...this.state.newsFeedItemList];
    const numOfFavoriteItem = itemArr.length;

    if (itemArr.length > 0) {
      itemArr.forEach((element) => {
        newsFeedItemList[element.index].isLiked = true;
      });
    }

    this.setState({ newsFeedItemList, numOfFavoriteItem });
  };

  render() {
    const { children } = this.props;

    return (
      <div>
        <Navbar />
        {children}
      </div>
    );
  }
}
