import Head from "next/head";
import Layout from "../component/Layout";
import FavoriteItem from "../component/FavoriteItem";
import fetch from "isomorphic-fetch";
import styles from "../public/utils.module.css";

async function fetchData() {
  const response = await fetch("/api/some/endpoint");
  const favoriteData = await response.json();

  return { favoriteData };
}

export default function Favorite(props) {
  const [favoriteData, setFavoriteData] = useState(props.setFavoriteData);

  async function refresh() {
    const refreshedProps = await fetchData();
    setFavoriteData(refreshedProps.favoriteData);
  }

  return (
    <Layout>
      <div>
        <Head>
          <title>Favorites</title>
        </Head>
        <div className={styles.topClearance}>
          {favItem.length > 0 ? (
            favItem.forEach((element, key) => {
              <FavoriteItem key={key} detail={element} />;
            })
          ) : (
            <FavoriteItem />
          )}
        </div>
      </div>
    </Layout>
  );
}
